import re
import json
from tqdm import tqdm
from collections import Counter
from nltk import word_tokenize
from sklearn.feature_extraction.text import TfidfVectorizer, CountVectorizer


def produce_news_docs():
    with open('../../data/news/coba_news_amb.json') as f:
        for i, line in tqdm(enumerate(f)):
            yield i, json.loads(line)


def produce_news_articles():
    for i, doc in produce_news_docs():
        yield i, re.split(r'(To contact the reporters? on this story|'
                          r'To contact the writer of this article)',
                          doc['article'])[0]


def news_tfidf():
    articles = [a for _, a in produce_news_articles()]
    vectorizer = TfidfVectorizer(min_df=20, stop_words='english')
    vecs = vectorizer.fit_transform(articles)
    cvectorizer = CountVectorizer(vocabulary=vectorizer.get_feature_names())
    cvec = cvectorizer.fit_transform([' '.join(articles)])

    # ti = [(token, idf) for token, idf in zip(vectorizer.get_feature_names(), vectorizer.idf_)]
    ti = [(token, idf * tf) for token, idf, tf in
          zip(vectorizer.get_feature_names(), vectorizer.idf_, cvec.toarray()[0])]
    with open('../../data/news/vocab_idf.csv', 'w') as f:
        for token, cnt in sorted(ti, key=lambda x: x[1], reverse=False):
            # f.write(f'{token},{cnt}\n')
            # f.write(f'{cnt * 1000:.0f},{token}\n')
            # f.write(f'{cnt:.0f},{token}\n')
            f.write(f'{cnt:.0f},{token}\n')


def news_dictionary():
    vocab = Counter()
    for i, article in produce_news_articles():
        tokens = word_tokenize(article)
        vocab += Counter(tokens)

    with open('../../data/news/vocab.csv', 'w') as f:
        for token, cnt in vocab.most_common():
            # f.write(f'{token},{cnt}\n')
            f.write(f'{cnt} {token}\n')


if __name__ == '__main__':
    # news_dictionary()
    news_tfidf()

